  //         transaction={transaction}
  //         loading={loading}
  //         setTransactionApproval={setTransactionApproval}
  //       />
  //     ))}
  //   </div>
  // )
  return (
    <TransactionPane
  key={transaction.id}
  transaction={transaction}
  loading={loading}
  setTransactionApproval={(transactionId, newValue) => {
    setTransactionApproval({
      transactionId,
      newValue,
    })
  }}
  onChange={(newValue) => setApproved(newValue)}
/>

  )
}
